import React, { useState, useEffect, N} from "react";
import { GoogleMap, LoadScript, StandaloneSearchBox } from "@react-google-maps/api";
import './LocationModal.css';

// The Modal component
const LocationModal = ({ isOpen, onClose }) => {
  const [locationPermission, setLocationPermission] = useState(null);
  const [address, setAddress] = useState("");
  const [manualSearch, setManualSearch] = useState(false);

  // Check for location permission when the component mounts
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        () => setLocationPermission("granted"),
        () => setLocationPermission("denied")
      );
    }
  }, []);

  // Handle enabling location permission
  const enableLocation = () => {
    navigator.geolocation.getCurrentPosition(
      (position) => {
        setAddress(`Latitude: ${position.coords.latitude}, Longitude: ${position.coords.longitude}`);
      },
      (error) => {
        console.error(error);
      }
    );
  };

  // Handle manual address search
  const searchManually = (newAddress) => {
    setAddress(newAddress);
  };

  const handleClose = () => {
    navigate("/LocationSelection");
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>Location Permission</h2>
        <p>
          {locationPermission === "granted"
            ? "Location permission granted"
            : "Location permission denied"}
        </p>

        {/* Buttons for actions */}
        <button onClick={enableLocation}>Enable Location</button>
        <button onClick={() => setManualSearch(true)}>Search Manually</button>

        {/* Conditional rendering for manual search */}
        {manualSearch && <ManualSearch onSearch={searchManually} />}
        
        <button onClick={handleClose}>Close</button>
      </div>
    </div>
  );
};

// The ManualSearch component with Google Maps integration
const ManualSearch = ({ onSearch }) => {
  const [searchBox, setSearchBox] = useState(null);

  const handlePlacesChanged = () => {
    const places = searchBox.getPlaces();
    if (places.length > 0) {
      const place = places[0];
      onSearch(place.formatted_address);
    }
  };

  return (
    <div className="search-container">
      <LoadScript googleMapsApiKey="AIzaSyADMDreOoidHSbnP7grvNILrvf3WKeMdjw">
        <GoogleMap
          id="manual-search-map"
          mapContainerStyle={{
            width: "100%",
            height: "400px",
          }}
          zoom={10}
        >
          <StandaloneSearchBox
            onLoad={(ref) => setSearchBox(ref)}
            onPlacesChanged={handlePlacesChanged}
          >
            <input
              type="text"
              placeholder="Search for an address"
              style={{
                boxSizing: `border-box`,
                border: `1px solid transparent`,
                width: `240px`,
                height: `32px`,
                padding: `0 12px`,
                borderRadius: `3px`,
                fontSize: `15px`,
                outline: `none`,
                textOverflow: `ellipses`,
              }}
            />
          </StandaloneSearchBox>
        </GoogleMap>
      </LoadScript>
    </div>
  );
};

export default LocationModal;
